ls /cygene/work/41.G35.G36.Beta/data/G3*E1L2_S*_L001_R2_001.fastq.gz  | while read line; do name=`ls $line |awk -F '/' '{print$NF}' |awk -F '_' '{print$1}'`; echo "mixcr analyze shotgun --align -OsaveOriginalReads=true --species hs --starting-material rna --receptor-type tcr  -r $name.report $line $name.mixcr.out"; echo "mkdir $name";echo "mixcr exportReadsForClones -s $name.mixcr.out.clna $name/$name."; echo "python /cygene/work/21.G17-library1/scripts/clonetype_support_umi.TSO.py $name.mixcr.out.clonotypes.TRX.txt /xxx/data/umi.fastq.gz  $name.umi.count.aa.all.xls"; echo "python /cygene/work/00.test/pipeline/TCR_Library_3_pipeline/scripts/merge_umi.py $name.umi.count.aa.all.xls >$name.merged.umi.count.xls" ; echo "Rscript /cygene/work/00.test/pipeline/TCR_Library_1_pipeline/scripts/histogram.R *.merged.umi.count.xls"; done >work.sh
